import {
    AbstractWalletPlugin,
    CallbackPayload,
    Checksum256,
    LoginContext,
    Logo,
    PermissionLevel,
    PromptElement,
    PublicKey,
    ResolvedSigningRequest,
    TransactContext,
    WalletPluginConfig,
    WalletPluginLoginResponse,
    WalletPluginMetadata,
    WalletPluginSignResponse,
} from '@wharfkit/session'
import {
    createIdentityRequest,
    waitForCallback,
    verifyLoginCallbackResponse,
} from '@wharfkit/protocol-esr'

export class WalletPluginPayCash extends AbstractWalletPlugin {
    chain: Checksum256 | undefined
    auth: PermissionLevel | undefined
    requestKey: PublicKey | undefined
    privateKey: unknown
    signerKey: PublicKey | undefined
    channelUrl: string | undefined
    channelName: string | undefined
    buoyUrl: string

    id = 'paycash'

    constructor(options?: { buoyUrl?: string }) {
        super()
        this.buoyUrl = options?.buoyUrl || 'https://cb.anchor.link'
    }

    readonly config: WalletPluginConfig = {
        requiresChainSelect: false,
        requiresPermissionSelect: false,
    }

    readonly metadata: WalletPluginMetadata = WalletPluginMetadata.from({
        name: 'PayCash',
        description: 'PayCash wallet plugin for WharfKit SessionKit',
        logo: Logo.from({
            dark: `data:image/svg+xml;base64,`,
            light: `data:image/svg+xml;base64,`,
        }),
        homepage: 'https://paycash.app',
        download: 'https://paycash.app',
    })

    async login(context: LoginContext): Promise<WalletPluginLoginResponse> {
        if (!context.ui) {
            throw new Error('No UI available')
        }
        const { callback, request, requestKey, privateKey } =
            await createIdentityRequest(context, this.buoyUrl)
        const elements: PromptElement[] = [
            {
                type: 'qr',
                data: request.encode(true, false, 'esr:'),
            },
        ]
        const promptResponse = context.ui.prompt({
            title: 'Connect with PayCash',
            body: 'Scan the QR code with PayCash on your mobile device to connect.',
            elements,
        })
        promptResponse.catch(() => {})
        const callbackResponse: CallbackPayload = await waitForCallback(callback, undefined, undefined)
        verifyLoginCallbackResponse(callbackResponse, context)
        if (!callbackResponse.cid || !callbackResponse.sa || !callbackResponse.sp) {
            throw new Error('Invalid callback response')
        }
        this.requestKey = requestKey
        this.privateKey = privateKey
        this.signerKey = callbackResponse.link_key ? PublicKey.from(callbackResponse.link_key) : undefined
        this.channelUrl = callbackResponse.link_ch
        this.channelName = callbackResponse.link_name
        const resolvedResponse = await ResolvedSigningRequest.fromPayload(
            callbackResponse,
            context.esrOptions
        )
        const identityProof = resolvedResponse.getIdentityProof(callbackResponse.sig)
        return {
            chain: Checksum256.from(callbackResponse.cid),
            permissionLevel: PermissionLevel.from({
                actor: callbackResponse.sa,
                permission: callbackResponse.sp,
            }),
            identityProof,
        }
    }

    async sign(
        resolved: ResolvedSigningRequest,
        context: TransactContext
    ): Promise<WalletPluginSignResponse> {
        if (!context.ui) {
            throw new Error('No UI available')
        }
        // Create a signing request for the transaction
        const modifiedRequest = await context.createRequest({ transaction: resolved.transaction })
        // Generate ESR URI for the transaction
        const esrUri = modifiedRequest.encode(true, false, 'esr:')
        // Show QR code to the user for cold wallet signing
        const elements: PromptElement[] = [
            {
                type: 'qr',
                data: esrUri,
            },
            {
                type: 'textarea',
                label: 'Instructions',
                data: {
                    value: 'Scan this QR code with your PayCash wallet to sign the transaction. After signing, paste the signature below.',
                    readonly: true,
                }
            },
            {
                type: 'textarea',
                label: 'Signature',
                data: {
                    placeholder: 'Paste the signature from PayCash here',
                }
            },
        ]
        // Prompt the user and wait for signature input
        const promptResponse = await context.ui.prompt({
            title: 'Sign Transaction with PayCash',
            body: 'Scan the QR code with PayCash, sign the transaction, and paste the signature below.',
            elements,
        })
        // Extract the signature from the textarea (use label as key, cast to any to avoid linter error)
        const signature = (promptResponse as any)['Signature'] || ''
        if (!signature) {
            throw new Error('No signature provided. Please sign the transaction in PayCash and paste the signature.')
        }
        // Return the signature in the expected format
        return {
            signatures: [signature],
        }
    }
} 